mapper.select:
SELECT COUNT(*) 
FROM userinfo
WHERE userEmail = #{userEmail}

SELECT COUNT(*) 
FROM userinfo
WHERE userPN = #{userPhoneNum}

SELECT userEmail
FROM userinfo
WHERE emailHash = #{code}

mapper.update:
UPDATE userinfo
SET emailcertification = 1
WHERE userEmail = #{userEmail}

UPDATE userinfo 
set userPwd = #{changePassword}
WHERE userEmail = #{userEmail}

mapper.select:
SELECT userEmail, userNickName, userImage 
FROM userinfo 
WHERE userEmail = #{userEmail}
